import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [lyrics, setLyrics] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  async function handleSearch() {
    setIsLoading(true);
    setError(null);

    try {
      const response = await axios.get('https://api.genius.com/songs/1/lyrics', {
        headers: {
          Authorization: 'Bearer YOUR_ACCESS_TOKEN_HERE',
        },
      });
      const lyrics = response.data.response.lyrics;
      setLyrics(lyrics);
    } catch (error) {
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  }

  return (
    <div className="App">
      <button onClick={handleSearch} disabled={isLoading}>
        Generate Lyrics
      </button>
      {isLoading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      {lyrics ? <p>{lyrics}</p> : <p>No lyrics to display</p>}
    </div>
  );
}

export default App;
